import React, { useState } from 'react'
import CartHeader from '../cartHeader/cartHeader'
import Footer from '../footer/footer'
import Cart from '../cart/cart'
import {products} from '../../helpers/product'
const CartBody = () => {
  const [cart, setCart] = useState(products)
  // удаление товара
  const deleteProduct = (id) => {
    setCart((cart) => cart.filter((tovar) => id !== tovar.id)
    )
  }

  // увеличение товаров

  const increase = (id) => {  // как и в предыдущем способе, ориентируемся на текущее состояние корзины
    console.log('increase', id)
    setCart((cart) => {  // основываемся на текущее состояние
      return cart.map((tovar) => {  // проходим текушую корзину и возвращаем новый масив 
        if (tovar.id === id) {  // если айди товара равен 
          return {
            ...tovar,
            count: tovar.count + 1
          }
        }
        return tovar
      })
    })
  }

  const tovars = cart.map((tovar) => {
    return( <Cart tovar={tovar} key={tovar.id} deleteProduct={deleteProduct} increase={increase}/>)
  })


  return (
    <div className="section-cart__body">
    <div className="container">

        <section className="cart">
            <CartHeader/>
           {tovars}
            
            <Footer/>

        </section>

    </div>
</div>
  )
}

export default CartBody
